package com.zifang.util.zex.leetcode;

import java.util.ArrayList;

public class _392 {
    public boolean isSubsequence(String s, String t) {
        int tIndex = 0;
        for(int i = 0; i< s.length(); i++){
            // 被比较char
            char c = s.charAt(i);
            boolean continueFlag= false;

            // 长度已经不够比较了就提前返回
            if(t.length() -tIndex < s.length() - i){
                return false;
            }

            // 从上次遍历开始 目标串内是否存在
            for(int j = tIndex; j< t.length(); j++){
                if(c == t.charAt(j)){
                    tIndex = j+1;
                    continueFlag = true;
                    continue;
                }
            }
            if(continueFlag){
                continue;
            }  else {
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        new _392().isSubsequence("leeeeetcode","");


//        r.add(new ArrayList(Arrays.as(){1, 1, 1}));

    }
}
